import React, { useState, useEffect, useRef } from "react";
import { Routes, Route, useLocation, useNavigate } from "react-router-dom";
import Header from "./components/Header";
import HomePage from "./pages/HomePage";
import { FilmPage } from "./components/FilmPage";
import SettingsPage from "./components/SettingsPage";

function OutsideClickWrapper({ children, redirectTo = "/" }) {
  const navigate = useNavigate();
  const wrapperRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true);
    const handleClickOutside = (event) => {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setVisible(false);
        setTimeout(() => navigate(redirectTo), 300);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [navigate, redirectTo]);

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center transition-all duration-300 ${visible ? "bg-black/60 backdrop-blur-sm opacity-100" : "opacity-0 pointer-events-none"}`}>
      <div ref={wrapperRef} className={`bg-zinc-900 p-6 rounded-lg shadow-lg w-full max-w-3xl transform transition-all duration-300 ${visible ? "scale-100 opacity-100" : "scale-95 opacity-0"}`}>
        {children}
      </div>
    </div>
  );
}

function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [showUsers, setShowUsers] = useState(false);
  const userMenuRef = useRef();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutsideSettings = (e) => {
      const settingsWrapper = document.querySelector(".settings-wrapper");
      if (location.pathname === "/settings" && settingsWrapper && !settingsWrapper.contains(e.target)) {
        navigate("/");
      }
    };
    document.addEventListener("mousedown", handleClickOutsideSettings);
    return () => document.removeEventListener("mousedown", handleClickOutsideSettings);
  }, [location, navigate]);

  return (
    <div className={`${darkMode ? "bg-black text-white" : "bg-white text-black"} min-h-screen flex flex-col`}>
      <Header
        darkMode={darkMode}
        toggleDarkMode={() => setDarkMode(!darkMode)}
        showUsers={showUsers}
        setShowUsers={setShowUsers}
        userMenuRef={userMenuRef}
      />
      <main className="flex-1 overflow-y-auto p-6 space-y-10 relative">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/film/:id" element={<OutsideClickWrapper><FilmPage /></OutsideClickWrapper>} />
          <Route path="/movies" element={<h2 className="text-xl">Movies Page (în lucru)</h2>} />
          <Route path="/tv-shows" element={<h2 className="text-xl">TV Shows Page (în lucru)</h2>} />
          <Route path="/search" element={<h2 className="text-xl">Search Page (în lucru)</h2>} />
          <Route path="/settings" element={<OutsideClickWrapper redirectTo="/"><div className="settings-wrapper"><SettingsPage /></div></OutsideClickWrapper>} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
