import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import FilmCard from "../components/FilmCard";
import films from "../data/films";

function OutsideClickWrapper({ children, redirectTo = "/search" }) {
  const navigate = useNavigate();
  const wrapperRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true);
    const handleClickOutside = (event) => {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setVisible(false);
        setTimeout(() => navigate(redirectTo), 300);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [navigate, redirectTo]);

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center transition-all duration-300 ${visible ? "bg-black/60 backdrop-blur-sm opacity-100" : "opacity-0 pointer-events-none"}`}>
      <div
        ref={wrapperRef}
        className={`bg-zinc-900 p-6 rounded-lg shadow-lg w-full max-w-6xl transform transition-all duration-300 ${visible ? "scale-100 opacity-100" : "scale-95 opacity-0"}`}
      >
        {children}
      </div>
    </div>
  );
}

function SearchPage() {
  const [query, setQuery] = useState("");
  const [genreFilter, setGenreFilter] = useState("");
  const [ratingFilter, setRatingFilter] = useState("");
  const [selectedFilm, setSelectedFilm] = useState(null);

  const filteredFilms = films.filter((film) => {
    const matchesQuery = film.title.toLowerCase().includes(query.toLowerCase());
    const matchesGenre = genreFilter ? film.genre === genreFilter : true;
    const matchesRating = ratingFilter ? parseFloat(film.rating?.split(" ")[1]) >= parseFloat(ratingFilter) : true;
    return matchesQuery && matchesGenre && matchesRating;
  });

  return (
    <div className="space-y-6">
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Search films..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="px-4 py-2 rounded bg-zinc-800 text-white placeholder-zinc-400 w-full max-w-xs"
        />
        <select
          value={genreFilter}
          onChange={(e) => setGenreFilter(e.target.value)}
          className="px-4 py-2 rounded bg-zinc-800 text-white"
        >
          <option value="">All Genres</option>
          <option value="Sci-Fi">Sci-Fi</option>
          <option value="Action">Action</option>
          <option value="Drama">Drama</option>
          <option value="Thriller">Thriller</option>
          <option value="Mystery">Mystery</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Adventure">Adventure</option>
        </select>
        <select
          value={ratingFilter}
          onChange={(e) => setRatingFilter(e.target.value)}
          className="px-4 py-2 rounded bg-zinc-800 text-white"
        >
          <option value="">All Ratings</option>
          <option value="4.5">⭐ 4.5+</option>
          <option value="4.0">⭐ 4.0+</option>
          <option value="3.5">⭐ 3.5+</option>
        </select>
      </div>

      <div className="flex flex-wrap gap-6">
        {filteredFilms.map((film) => (
          <div key={film.id} onClick={() => setSelectedFilm(film)}>
            <FilmCard film={film} />
          </div>
        ))}
      </div>

      {selectedFilm && (
        <OutsideClickWrapper redirectTo="/search">
          <div>
            <h2 className="text-3xl font-bold mb-4">{selectedFilm.title}</h2>
            <p className="mb-2 text-zinc-300">{selectedFilm.description}</p>
            <p className="text-sm text-zinc-400">{selectedFilm.duration} • {selectedFilm.genre} • {selectedFilm.rating}</p>
          </div>
        </OutsideClickWrapper>
      )}
    </div>
  );
}

export default SearchPage;
