import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";

function OutsideClickWrapper({ children, redirectTo = "/" }) {
  const navigate = useNavigate();
  const wrapperRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true);
    const handleClickOutside = (event) => {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setVisible(false);
        setTimeout(() => navigate(redirectTo), 300);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [navigate, redirectTo]);

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center transition-all duration-300 ${visible ? "bg-black/60 backdrop-blur-sm opacity-100" : "opacity-0 pointer-events-none"}`}>
      <div
        ref={wrapperRef}
        className={`bg-zinc-900 p-8 rounded-xl shadow-2xl w-[90vw] max-w-[1200px] transform transition-all duration-300 ${visible ? "scale-100 opacity-100" : "scale-95 opacity-0"}`}

      >
        {children}
      </div>
    </div>
  );
}

export default OutsideClickWrapper;
