import React, { useState, useEffect, useRef } from "react";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import HomePage from "./pages/HomePage";
import SearchPage from "./pages/SearchPage";
import FilmPage from "./components/FilmPage";
import { SettingsPage } from "./components/SettingsPage";
import OutsideClickWrapper from "./components/OutsideClickWrapper";
import Header from "./components/Header";

function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [showUsers, setShowUsers] = useState(false);
  const userMenuRef = useRef();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    function handleClickOutside(event) {
      if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
        setShowUsers(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    const handleClickOutsideSettings = (e) => {
      const settingsWrapper = document.querySelector(".settings-wrapper");
      if (location.pathname === "/settings" && settingsWrapper && !settingsWrapper.contains(e.target)) {
        navigate("/");
      }
    };
    document.addEventListener("mousedown", handleClickOutsideSettings);
    return () => document.removeEventListener("mousedown", handleClickOutsideSettings);
  }, [location, navigate]);

  useEffect(() => {
    const handleClickOutsideFilm = (e) => {
      const filmPageWrapper = document.querySelector(".film-page");
      if (location.pathname.startsWith("/film/") && filmPageWrapper && !filmPageWrapper.contains(e.target)) {
        if (document.referrer.includes("/search")) {
          navigate("/search");
        } else {
          navigate("/");
        }
      }
    };
    document.addEventListener("mousedown", handleClickOutsideFilm);
    return () => document.removeEventListener("mousedown", handleClickOutsideFilm);
  }, [location, navigate]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={`${darkMode ? "bg-black text-white" : "bg-white text-black"} min-h-screen flex flex-col`}>
      <Header
        darkMode={darkMode}
        toggleDarkMode={toggleDarkMode}
        showUsers={showUsers}
        setShowUsers={setShowUsers}
        userMenuRef={userMenuRef}
      />

      <main className="flex-1 overflow-y-auto p-6 space-y-10 relative">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/film/:id" element={<OutsideClickWrapper redirectTo={location.state?.from || "/"}><div className="film-page"><FilmPage /></div></OutsideClickWrapper>} />
          <Route path="/movies" element={<h2 className="text-xl">Movies Page (în lucru)</h2>} />
          <Route path="/tv-shows" element={<h2 className="text-xl">TV Shows Page (în lucru)</h2>} />
          <Route path="/search" element={<SearchPage />} />
          <Route path="/settings" element={<OutsideClickWrapper redirectTo="/"><div className="settings-wrapper"><SettingsPage /></div></OutsideClickWrapper>} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
