import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useLocation } from "react-router-dom";
import { Search, Home, Film, Tv, User, Moon, Sun, Settings } from "lucide-react";

import dreamsPoster from "./assets/posters/dreams.jpg";
import eternalDawnPoster from "./assets/posters/eternal-dawn.jpg";
import automataPoster from "./assets/posters/automata.jpg";
import aiHunterPoster from "./assets/posters/ai-hunter.jpg";
import neonStormPoster from "./assets/posters/neon-storm.jpg";
import futureEdgePoster from "./assets/posters/future-edge.jpg";
import darkProtocolPoster from "./assets/posters/dark-protocol.jpg";
import digitalEdenPoster from "./assets/posters/digital-eden.jpg";
import quantumDriftPoster from "./assets/posters/quantum-drift.jpg";
import zeroHourPoster from "./assets/posters/zero-hour.jpg";
import spectrumPoster from "./assets/posters/spectrum.jpg";
import cyberDreamPoster from "./assets/posters/cyber-dream.jpg";
import silentCircuitPoster from "./assets/posters/silent-circuit.jpg";
import parallelPoster from "./assets/posters/parallel.jpg";
import skyHackerPoster from "./assets/posters/sky-hacker.jpg";
import aiHunterPreview from "./assets/previews/ai-hunter.mp4";
import { FilmPage } from "./components/FilmPage";
import { SettingsPage } from "./components/SettingsPage";

function FilmCard({ film }) {
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);

  return (
    <Link
      to={`/film/${film.id}`}
      className="group relative rounded overflow-hidden transition-all duration-300 aspect-video"
      style={{ width: isHovered ? '36rem' : '18rem' }}
      onMouseEnter={() => {
        setIsHovered(true);
        if (videoRef.current) {
          videoRef.current.currentTime = 0;
          videoRef.current.play();
        }
      }}
      onMouseLeave={() => {
        setIsHovered(false);
        if (videoRef.current) {
          videoRef.current.pause();
        }
      }}
    >
      {film.previewUrl && isHovered ? (
        <video
          ref={videoRef}
          src={film.previewUrl}
          muted
          className="w-full h-full object-cover transition-all duration-300"
        />
      ) : (
        <img
          src={film.image}
          alt={film.title}
          className="w-full h-full object-cover transition-all duration-300"
        />
      )}
    </Link>
  );
}

const films = [
  { id: "dreams", title: "Dreams", genre: "Sci-Fi", image: dreamsPoster },
  { id: "eternal-dawn", title: "Eternal Dawn", genre: "Action", image: eternalDawnPoster },
  { id: "automata", title: "Automata", genre: "Thriller", image: automataPoster },
  { id: "ai-hunter", title: "AI Hunter", genre: "Drama", image: aiHunterPoster, previewUrl: aiHunterPreview },
  { id: "neon-storm", title: "Neon Storm", genre: "Fantasy", image: neonStormPoster },
  { id: "future-edge", title: "Future Edge", genre: "Sci-Fi", image: futureEdgePoster },
  { id: "dark-protocol", title: "Dark Protocol", genre: "Mystery", image: darkProtocolPoster },
  { id: "digital-eden", title: "Digital Eden", genre: "Sci-Fi", image: digitalEdenPoster },
  { id: "quantum-drift", title: "Quantum Drift", genre: "Adventure", image: quantumDriftPoster },
  { id: "zero-hour", title: "Zero Hour", genre: "Thriller", image: zeroHourPoster },
  { id: "spectrum", title: "Spectrum", genre: "Drama", image: spectrumPoster },
  { id: "cyber-dream", title: "Cyber Dream", genre: "Sci-Fi", image: cyberDreamPoster },
  { id: "silent-circuit", title: "Silent Circuit", genre: "Mystery", image: silentCircuitPoster },
  { id: "parallel", title: "Parallel", genre: "Fantasy", image: parallelPoster },
  { id: "sky-hacker", title: "Sky Hacker", genre: "Action", image: skyHackerPoster },
];

function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [showUsers, setShowUsers] = useState(false);
  const userMenuRef = useRef();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    function handleClickOutside(event) {
      if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
        setShowUsers(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    const handleClickOutsideSettings = (e) => {
      const settingsWrapper = document.querySelector(".settings-wrapper");
      if (location.pathname === "/settings" && settingsWrapper && !settingsWrapper.contains(e.target)) {
        navigate("/");
      }
    };
    document.addEventListener("mousedown", handleClickOutsideSettings);
    return () => document.removeEventListener("mousedown", handleClickOutsideSettings);
  }, [location, navigate]);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={`${darkMode ? "bg-black text-white" : "bg-white text-black"} min-h-screen flex flex-col`}>
      <header className="w-full bg-gradient-to-b from-zinc-900 via-zinc-800 to-zinc-900 p-4 flex items-center justify-between">
        <div className="flex items-center gap-10">
          <h1 className="text-2xl font-bold text-white">
            Cinem<span className="text-cyan-400">AI</span>
          </h1>
          <nav className="flex gap-6 text-white">
            <Link to="/" className="flex items-center gap-2 hover:text-cyan-400"><Home size={20} /> Home</Link>
            <Link to="/movies" className="flex items-center gap-2 hover:text-cyan-400"><Film size={20} /> Movies</Link>
            <Link to="/tv-shows" className="flex items-center gap-2 hover:text-cyan-400"><Tv size={20} /> TV Shows</Link>
            <Link to="/search" className="flex items-center gap-2 hover:text-cyan-400"><Search size={20} /> Search</Link>
          </nav>
        </div>
        <div className="flex items-center gap-4 relative" ref={userMenuRef}>
          <button onClick={toggleDarkMode} className="hover:scale-110 transition-transform text-white">
            {darkMode ? <Sun size={20} /> : <Moon size={20} />}
          </button>
          <button onClick={() => setShowUsers(!showUsers)} className="hover:scale-110 transition-transform text-white">
            <User size={24} />
          </button>
          <div className={`absolute right-0 top-full mt-2 z-50 bg-zinc-800 text-white rounded shadow-lg w-48 p-4 transition-all duration-300 ease-in-out transform origin-top ${showUsers ? "scale-y-100 opacity-100 pointer-events-auto" : "scale-y-0 opacity-0 pointer-events-none"}`}>
            <h3 className="font-semibold mb-2">Users</h3>
            <ul className="space-y-2">
              <li>User 1</li>
              <li>User 2</li>
              <li>User 3</li>
            </ul>
            <hr className="my-2 border-zinc-600" />
            <Link to="/settings" className="flex items-center gap-2 text-sm hover:text-blue-400 px-2 py-1 cursor-pointer" onClick={() => setShowUsers(false)}><Settings size={16} /> Setări</Link>
          </div>
        </div>
      </header>

      <main className="flex-1 overflow-y-auto p-6 space-y-10 relative">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/film/:id" element={<OutsideClickWrapper><FilmPage films={films} /></OutsideClickWrapper>} />
          <Route path="/movies" element={<h2 className="text-xl">Movies Page (în lucru)</h2>} />
          <Route path="/tv-shows" element={<h2 className="text-xl">TV Shows Page (în lucru)</h2>} />
          <Route path="/search" element={<h2 className="text-xl">Search Page (în lucru)</h2>} />
          <Route path="/settings" element={<div className="settings-wrapper"><SettingsPage /></div>} />
        </Routes>
      </main>
    </div>
  );
}

function OutsideClickWrapper({ children, redirectTo = "/" }) {
  const navigate = useNavigate();
  const wrapperRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true);
    const handleClickOutside = (event) => {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setVisible(false);
        setTimeout(() => navigate(redirectTo), 300);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [navigate, redirectTo]);

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center transition-all duration-300 ${visible ? "bg-black/60 backdrop-blur-sm opacity-100" : "opacity-0 pointer-events-none"}`}>
      <div ref={wrapperRef} className={`bg-zinc-900 p-6 rounded-lg shadow-lg w-full max-w-3xl transform transition-all duration-300 ${visible ? "scale-100 opacity-100" : "scale-95 opacity-0"}`}>
        {children}
      </div>
    </div>
  );
}

function Section({ title, items }) {
  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">{title}</h2>
      <div className="flex flex-wrap gap-6">
        {items.map((film) => (
          <FilmCard key={film.id} film={film} />
        ))}
      </div>
    </div>
  );
}

function HomePage() {
  return (
    <div className="space-y-10">
      <Section title="Continue Watching" items={films.slice(0, 4)} />
      <Section title="Period Pieces" items={films.slice(4, 8)} />
      <Section title="New on CinemAI" items={films.slice(8, 12)} />
      <Section title="TV Action & Adventure" items={films.slice(12, 16)} />
    </div>
  );
}

export default App;
