import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";

function FilmCard({ film }) {
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);
  const [isClicked, setIsClicked] = useState(false);

  return (
    <Link
      to={`/film/${film.id}`}
      className={`group relative rounded overflow-hidden transition-all duration-300 aspect-video ${isClicked ? "scale-110 z-10" : ""}`}
      style={{ width: isHovered ? "36rem" : "18rem" }}
      onMouseEnter={() => {
        setIsHovered(true);
        if (videoRef.current) {
          videoRef.current.currentTime = 0;
          videoRef.current.play();
        }
      }}
      onMouseLeave={() => {
        setIsHovered(false);
        if (videoRef.current) {
          videoRef.current.pause();
        }
      }}
      onMouseDown={() => setIsClicked(true)}
      onMouseUp={() => setTimeout(() => setIsClicked(false), 150)}
    >
      {film.previewUrl && isHovered ? (
        <video
          ref={videoRef}
          src={film.previewUrl}
          muted
          className="w-full h-full object-cover transition-all duration-300"
        />
      ) : (
        <img
          src={film.image}
          alt={film.title}
          className="w-full h-full object-cover transition-all duration-300"
        />
      )}
      <div className="absolute bottom-0 w-full bg-black/70 text-white text-sm p-1 text-center">
        {film.title}
      </div>
    </Link>
  );
}

export default FilmCard;
