import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import FilmCard from "../components/FilmCard";
import films from "../data/films";

function OutsideClickWrapper({ children, redirectTo = "/search" }) {
  const navigate = useNavigate();
  const wrapperRef = useRef(null);
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setVisible(true);
    const handleClickOutside = (event) => {
      const userMenu = document.querySelector("[data-user-menu]");
      if (
        wrapperRef.current &&
        !wrapperRef.current.contains(event.target) &&
        (!userMenu || !userMenu.contains(event.target))
      ) {
        setVisible(false);
        setTimeout(() => navigate(redirectTo), 300);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [navigate, redirectTo]);

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center transition-all duration-300 ${visible ? "bg-black/60 backdrop-blur-sm opacity-100" : "opacity-0 pointer-events-none"}`}>
      <div
        ref={wrapperRef}
        className={`bg-zinc-900 p-6 rounded-lg shadow-lg w-full max-w-3xl transform transition-all duration-300 ${visible ? "scale-100 opacity-100" : "scale-95 opacity-0"}`}
      >
        {children}
      </div>
    </div>
  );
}

// rest of the file rămâne neschimbat

export default SearchPage;
