import React, { useState, useEffect } from "react";
import FilmCard from "../components/FilmCard";
import films from "../data/films"; // presupunem că ai extras lista într-un fișier separat

function SearchPage() {
  const [query, setQuery] = useState("");
  const [mounted, setMounted] = useState(false);
  const [clickedFilmId, setClickedFilmId] = useState(null);

  useEffect(() => {
    const timeout = setTimeout(() => setMounted(true), 10);
    return () => clearTimeout(timeout);
  }, []);

  const filteredFilms = films.filter((film) =>
    film.title.toLowerCase().includes(query.toLowerCase())
  );

  const handleFilmClick = (id) => {
    setClickedFilmId(id);
  };

  return (
    <div className={`transition-all duration-700 ease-out transform ${mounted ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"}`}>
      <h2 className="text-2xl font-bold mb-4">Search</h2>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for a film..."
        className="w-full p-2 rounded bg-zinc-800 text-white placeholder-zinc-400 mb-6 transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-cyan-400"
      />
      <div className="flex flex-wrap gap-6">
        {filteredFilms.length > 0 ? (
          filteredFilms.map((film) => (
            <div
              key={film.id}
              className={`transition-all duration-300 ${clickedFilmId === film.id ? "scale-110 z-10" : "scale-100"}`}
              onClick={() => handleFilmClick(film.id)}
            >
              <FilmCard film={film} />
            </div>
          ))
        ) : (
          <p className="text-gray-400">No films found.</p>
        )}
      </div>
    </div>
  );
}

export default SearchPage;
