import React from "react";
import FilmCard from "./FilmCard";

function TopFive({ films }) {
  const topFive = films.slice(0, 5);

  return (
    <div className="space-y-4">
      <h2 className="text-3xl font-bold text-white px-4">TOP 5 MOVIES TODAY</h2>
      <div className="flex gap-6 overflow-x-auto px-4">
        {topFive.map((film, index) => (
          <div key={film.id} className="relative">
            <div className="absolute -left-6 top-4 text-[120px] font-black text-gray-800 opacity-20 select-none">
              {index + 1}
            </div>
            <div className="w-40 md:w-48">
              <img
                src={film.poster}
                alt={film.title}
                className="rounded-xl shadow-lg hover:scale-105 transition-transform duration-300"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopFive;
