// FilmCard.js
import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function FilmCard({ film }) {
  const location = useLocation();
  const fromSearch = location.pathname === "/search";

  return (
    <Link
      to={`/film/${film.id}`}
      state={fromSearch ? { from: "/search" } : undefined}
      className="film-card"
    >
      <img src={film.image} alt={film.title} className="film-poster" />
      <h3>{film.title}</h3>
      <p className="text-sm opacity-70">{film.genre}</p>
    </Link>
  );
}

export default FilmCard;
