import React, { useState } from "react";

export function SettingsPage() {
  const [language, setLanguage] = useState("ro");
  const [quality, setQuality] = useState("auto");
  const [subtitles, setSubtitles] = useState(true);

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-zinc-800 text-white rounded-lg shadow-lg space-y-6">
      <h2 className="text-2xl font-bold">Setări cont</h2>

      <div>
        <label className="block mb-1 font-semibold">Limba</label>
        <select
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
          className="w-full bg-zinc-700 text-white p-2 rounded"
        >
          <option value="ro">Română</option>
          <option value="en">Engleză</option>
          <option value="es">Spaniolă</option>
          <option value="fr">Franceză</option>
        </select>
      </div>

      <div>
        <label className="block mb-1 font-semibold">Calitate Video</label>
        <select
          value={quality}
          onChange={(e) => setQuality(e.target.value)}
          className="w-full bg-zinc-700 text-white p-2 rounded"
        >
          <option value="auto">Auto</option>
          <option value="480p">480p</option>
          <option value="720p">720p</option>
          <option value="1080p">1080p</option>
          <option value="4k">4K</option>
        </select>
      </div>

      <div className="flex items-center gap-3">
        <input
          type="checkbox"
          checked={subtitles}
          onChange={() => setSubtitles(!subtitles)}
        />
        <label className="font-semibold">Activează subtitrări implicite</label>
      </div>

      <button className="bg-red-600 hover:bg-red-700 transition-colors px-4 py-2 rounded">
        Salvează
      </button>
    </div>
  );
}
