import React from "react";
import { Link } from "react-router-dom";

export function FilmCard({ film, size }) {
  const cardSize = size === "small" ? "w-48" : "w-64";
  return (
    <Link to={`/film/${film.id}`} className={`block rounded overflow-hidden shadow-lg ${cardSize}`}>
      <div className={`h-64 bg-cover bg-center ${film.bgColor}`} style={{ backgroundImage: `url(${film.image})` }}>
        <div className="bg-black/50 p-2 text-white">{film.title}</div>
      </div>
    </Link>
  );
}
