import React from "react";

function TopFive({ films }) {
  const topFive = films.slice(0, 5);

  return (
    <div className="space-y-4 pl-16 overflow-x-auto">
      <h2 className="text-3xl font-extrabold text-white px-4 tracking-wide">
        TOP 5 MOVIES TODAY
      </h2>
      <div className="flex gap-10 min-w-max pr-4 py-4">
        {topFive.map((film, index) => (
          <div
            key={film.id}
            className="relative w-48 shrink-0 group hover:scale-105 transition-transform duration-300 pl-10"
          >
            <div className="absolute -left-20 top-0 text-[160px] font-extrabold text-white opacity-30 group-hover:opacity-50 drop-shadow-xl leading-none z-0">
              {index + 1}
            </div>
            <img
              src={film.poster}
              alt={film.title}
              className="w-full h-72 object-cover rounded-xl shadow-xl border-2 border-gray-800 group-hover:border-white relative z-10"
            />
            <div className="mt-2 text-center text-sm text-gray-300 group-hover:text-white">
              {film.title}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TopFive;
